import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("login").getPropertyValue("Response")

def holder = new JsonSlurper().parseText(Data)
def token = holder.accessToken
//Setting a varible in project level with the value

context.testCase.TestSuite.project.setPropertyValue("VT","bearer" +token)

import groovy.json.JsonSlurper
def vandana = context.testCase.getTestStepByName("get all users").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(vandana)
def num = holder.count.toInteger()
for(def i=0;i<num;i++)
{
	if(holder.users[i].email=="vishalreddy5588@gmail.com")
	{
		def uid = holder.users[i]._id
		//log.info uid
		def converted_uid = uid.toString()
		context.testCase.testSuite.project.setPropertyValue("uid",converted_uid)
		log.info converted_uid
	}
}

import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("get all products").getPropertyValue("Response")

def holder = new JsonSlurper().parseText(Data)
def num = holder.count.toInteger()
log.info num
for(def i=0;i<num;i++)
{
	if(holder.products[i].name=="bourbon biscuit")
	{
		def pid = holder.products[i]._id
		log.info pid
		def converted_pid = pid.toString()
		context.testCase.testSuite.project.setPropertyValue("product_id", converted_pid)
		log.info converted_pid
	}
}

